import { useState } from "react";
import "./modal.scss";
import Modal from "react-modal";
import DateTime from "react-datetime";

const AddEventModal = ({ isOpen, onClose, onEventAdded }) => {
  const [selectedProfessional, setSelectedProfessional] = useState("");
  const [date, setDate] = useState(new Date());

  const onSubmit = (event) => {
    event.preventDefault();

    onEventAdded({
      title: selectedProfessional,
      start: date, // Cambiamos 'date' a 'start'
    });

    onClose();
  };

  return (
    <Modal isOpen={isOpen} onRequestClose={onClose}>
      <form onSubmit={onSubmit} className="modal">
        <div className="section">
          <label>Seleccionar Profesional</label>
          <select value={selectedProfessional} onChange={(e) => setSelectedProfessional(e.target.value)}>
            <option value="" disabled>Seleccionar profesional</option>
            <option value="Fuentes Omar">Fuentes Omar</option>
            <option value="Lopez Mario">Lopez Mario</option>
            <option value="Pacheco Luisa">Pacheco Luisa</option>
            <option value="Parra Carlos">Parra Carlos</option>
            <option value="Rivas Luis">Rivas Luis</option>
          </select>
        </div>
        <div>
          <label>Ingresar Fecha</label>
          <DateTime value={date} onChange={(newDate) => setDate(newDate)} />
        </div>
        <button type="submit">Agregar</button>
      </form>
    </Modal>
  );
};

export default AddEventModal;
