// schedule.jsx

import "./schedule.scss";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import AddEventModal from "../modal/modal";
import { useState, useRef, useEffect } from "react";
import { useNavigate } from 'react-router-dom';
import Cookies from 'js-cookie';

const Schedule = () => {
  const [modalOpen, setModalOpen] = useState(false);
  const [events, setEvents] = useState([]);
  const calendarRef = useRef(null);
  const navigate = useNavigate();

  // Obtener Tipo de Calendario del Usuario desde el Token
  const tipoCalendarioDelUsuario = Cookies.get('calendar');

  // Traer Eventos según el Calendario del Usuario
  useEffect(() => {
    const getEvents = async () => {
      try {
        const response = await fetch(`http://localhost:8000/api/events/${tipoCalendarioDelUsuario}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${Cookies.get('token')}`,
          },
        });

        if (response.ok) {
          const eventosData = await response.json();

          // Formatear las fechas para eliminar la información de la hora
          const formattedEvents = eventosData.map(event => ({
            ...event,
            date: new Date(event.date).toISOString().split('T')[0], // Conservar solo la parte de la fecha
          }));

          setEvents(formattedEvents);
        } else {
          console.error("Error al obtener eventos");
        }
      } catch (error) {
        console.error("Error:", error);
      }
    };

    getEvents();
  }, [tipoCalendarioDelUsuario]);

  // Agregar Evento
  const onEventAdded = (event) => {
    let calendarApi = calendarRef.current.getApi();
    calendarApi.addEvent(event);
  };

  // Logout
  const handleLogout = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/logout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        console.log("Cierre de sesión exitoso");
        navigate('/');
      } else {
        console.error("Error al cerrar sesión");
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div className="schedule">
      <FullCalendar
        ref={calendarRef}
        plugins={[dayGridPlugin]}
        initialView="dayGridMonth"
        weekNumberCalculation="ISO"
        locale="es"
        height={600}
        headerToolbar={{
          left: "Add",
          center: "title",
          right: "today prev,next CloseSesion",
        }}
        weekNumbersWithinDays={5}
        customButtons={{
          Add: {
            text: "Agregar Guardia",
            click: () => setModalOpen(true),
          },
          CloseSesion: {
            text: "Cerrar Sesion",
            click: () => handleLogout(true),
          }
        }}
        events={events} // Mostrar eventos obtenidos según el tipo de calendario
        eventContent={({ event }) => (
          <div>
            <b>{event.title}</b>
          </div>
        )}
      />

      <AddEventModal
        isOpen={modalOpen}
        onClose={() => setModalOpen(false)}
        onEventAdded={(event) => onEventAdded(event)}
      />
    </div>
  );
};

export default Schedule;
