import "./schedule.scss";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import AddEventModal from "../modal/modal";
import { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import axios from 'axios';
import moment from 'moment';

const Schedule = () => {
  const [modalOpen, setModalOpen] = useState(false);
  const [events, setEvents] = useState([]);
  const calendarRef = useRef(null);
  const navigate = useNavigate();

  // Agregar Evento
  const onEventAdded = (event) => {
    let calendarApi = calendarRef.current.getApi();
    calendarApi.addEvent({
      date: moment(event.date).format("YYYY-MM-DD"),
      title: event.title
    });
  };

  //Agregar Evento a MongoDB
  const handleEventAdd = async (data) => {
    try {
      await axios.post("http://localhost:8000/api/calendarController/createEvent", data.event);
      console.log("Evento agregado con éxito");
    } catch (error) {
      console.error("Error al agregar el evento:", error);
    }
  };

  const handleDatesSet = async (data) => {
    const response = await axios.get(`http://localhost:8000/api/calendarController/getEvent?date=${moment(data.start).toISOString()}`);
    setEvents(response.data)
  };
  

  // Logout
  const handleLogout = async () => {
    try {
      const response = await fetch("http://localhost:8000/api/logout", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
      });

      if (response.ok) {
        console.log("Cierre de sesión exitoso");
        navigate("/");
      } else {
        console.error("Error al cerrar sesión");
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div className="schedule">
      <FullCalendar
        ref={calendarRef}
        events={events}
        plugins={[dayGridPlugin]}
        initialView="dayGridMonth"
        weekNumberCalculation="ISO"
        locale="es"
        height={600}
        headerToolbar={{
          left: "Add",
          center: "title",
          right: "today prev,next CloseSesion",
        }}
        weekNumbersWithinDays={5}
        customButtons={{
          Add: {
            text: "Agregar Guardia",
            click: () => setModalOpen(true),
          },
          CloseSesion: {
            text: "Cerrar Sesion",
            click: () => handleLogout(true),
          },
        }}
        eventAdd={(event) => handleEventAdd(event)} //agregar a base de datos
        datesSet={(date) => handleDatesSet(date)}
        eventContent={({ event }) => (
          <div>
            <b>{event.title}</b>
          </div>
        )}
      />

      <AddEventModal
        isOpen={modalOpen}
        onClose={() => setModalOpen(false)}
        onEventAdded={(event) => onEventAdded(event)}
      />
    </div>
  );
};

export default Schedule;
