import { useState } from "react";
import "./modal.scss";
import Modal from "react-modal";
import DateTime from "react-datetime"


const addGuardModal = ({ isOpen, onClose, onGuardAdded }) => {
  const [title, setTitle] = useState();
  const [date, setDate] = useState(new Date());

  const onSubmit = (event) => {
    event.preventDefault();

    onEventAdded({
      title,
      date,
    });
    onClose();
  };

  return (
    <Modal isOpen={isOpen} onRequestClose={onClose}>
      <form onSubmit="onSubmit">
        <select id="proffesional">
          <option value="" disabled selected>
            Selecciona una opción
          </option>
          <option value="title" onChange={(e) => setTitle(e.target.value)}>
            Opción 1
          </option>
          <option value="title">Opción 2</option>
          <option value="title">Opción 3</option>
          <option value="title">Opción 4</option>
        </select>
      </form>
      console.log("Ahora si papa")
    </Modal>
  );
};
export default addGuardModal;

