// schedule.jsx

import "./schedule.scss";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import AddEventModal from "../modal/modal";
import { useState, useRef, useEffect } from "react";
import { useNavigate } from 'react-router-dom';
import Cookies from 'js-cookie';

const Schedule = () => {
  const [modalOpen, setModalOpen] = useState(false);
  const [events, setEvents] = useState([]);
  const calendarRef = useRef(null);
  const navigate = useNavigate();

  // Mantener un registro de las fechas en las que se han creado eventos
  const eventDates = new Set();

  // Traigo tipo de calendario del usuario desde el Token
  const calendarType = Cookies.get('calendar');

  // Traigo el "Evento" según el calendario del usuario
  useEffect(() => {
    const getEvents = async () => {
      try {
        const response = await fetch(`http://localhost:8000/api/events/${calendarType}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${Cookies.get('token')}`,
          },
        });

        if (response.ok) {
          const eventsData = await response.json();
          setEvents(eventsData);
          // Actualiza el registro de fechas
          eventsData.forEach(event => {
            const eventDate = event.start.split('T')[0];
            eventDates.add(eventDate, eventDates.has(eventDate) ? eventDates.get(eventDate) + 1 : 1);
          });
        } else {
          console.error("Error al obtener eventos");
        }
      } catch (error) {
        console.error("Error:", error);
      }
    };

    getEvents();
  }, [calendarType]);

  // Agregar Evento
  const onEventAdded = (event) => {
    const eventDate = event.start.toISOString().split('T')[0]; // Obtener la fecha del evento

    if (eventDates.has(eventDate) && eventDates.get(eventDate) >= 2) {
      console.log("No se pueden agregar más de dos eventos en un día");
    } else {
      let calendarApi = calendarRef.current.getApi();
      calendarApi.addEvent(event);

      // Actualiza el registro de fechas
      eventDates.add(eventDate, eventDates.has(eventDate) ? eventDates.get(eventDate) + 1 : 1);

      // Actualiza el estado de eventos
      setEvents([...events, event]);

      // Guarda el evento en la base de datos (llamada al backend)
      saveEventToDatabase(event);
    }
  };

  // Función para guardar el evento en la base de datos
  const saveEventToDatabase = async (event) => {
    try {
      const response = await fetch('http://localhost:8000/api/events', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${Cookies.get('token')}`,
        },
        body: JSON.stringify({
          title: event.title,
          date: event.start.toISOString(), 
          calendar: calendarType,
        }),
      });

      if (response.ok) {
        console.log("Evento guardado en la base de datos");
      } else {
        console.error("Error al guardar el evento en la base de datos");
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  // Logout
  const handleLogout = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/logout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        console.log("Cierre de sesión exitoso");
        navigate('/');
      } else {
        console.error("Error al cerrar sesión");
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div className="schedule">
      <FullCalendar
        ref={calendarRef}
        plugins={[dayGridPlugin]}
        initialView="dayGridMonth"
        weekNumberCalculation="ISO"
        locale="es"
        height={600}
        headerToolbar={{
          left: "Add",
          center: "title",
          right: "today prev,next CloseSesion",
        }}
        weekNumbersWithinDays={5}
        customButtons={{
          Add: {
            text: "Agregar Guardia",
            click: () => setModalOpen(true),
          },
          CloseSesion: {
            text: "Cerrar Sesion",
            click: () => handleLogout(true),
          }
        }}
        events={events} // Muestro evento según tipo de calendario
        eventContent={({ event }) => (
          <div>
            <b>{event.title}</b>
          </div>
        )}
      />

      <AddEventModal
        isOpen={modalOpen}
        onClose={() => setModalOpen(false)}
        onEventAdded={(event) => onEventAdded(event)}
      />
    </div>
  );
};

export default Schedule;
