import { useState } from "react";
import "./modal.scss";
import Modal from "react-modal";
import DateTime from "react-datetime";

const AddGuardModal = ({ isOpen, onClose, onGuardAdded }) => {
  const [title, setTitle] = useState("");
  const [date, setDate] = useState(new Date());

  const onSubmit = (event) => {
    event.preventDefault();

    onGuardAdded({
      title,
      dale: date.toISOString(),
    });
    onClose();
  };

  return (
    <Modal isOpen={isOpen} onRequestClose={onClose}>
      <form onSubmit={onSubmit}>
        <input
          placeholder="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <div>
          <label>Ingresar Fecha</label>
          <DateTime value={date} onChange={(newDate) => setDate(newDate)} dateFormat="DD-MM-YYYY" timeFormat="HH:mm"/>
        </div>
        <button type="submit">Agregar</button>
      </form>
    </Modal>
  );
};
export default AddGuardModal;

