import { useState } from "react";
import "./modal.scss";
import Modal from "react-modal";
import DateTime from "react-datetime";

const addGuardModal = ({ isOpen, onClose, onGuardAdded }) => {
  const [title, setTitle] = useState();
  const [date, setDate] = useState(new Date());

  const onSubmit = (event) => {
    event.preventDefault();

    onEventAdded({
      title,
      date,
    });
    onClose();
  };

  return (
    <Modal isOpen={isOpen} onRequestClose={onClose}>
      <form onSubmit={onSubmit}>
        <input placeholder="text" value="title" onChange={(e) => setTitle(e.target.value)}/>
        <div>
            <label>Ingresar Fecha</label>
        <DateTime value={date} onChange={setDate(date)} />
        </div>
        <button>Agregar</button>
      </form>
    </Modal>
  );
};
export default addGuardModal;

